using System.Text;
using LiteDB;
using System.Threading;
using System.Collections.Generic;
using System;

namespace Chat.Tests {
    public abstract class BenchmarkTesterBase : ATester {
        protected string runIndexIdentifier;
        protected List<string> usernames = new();
        protected Random rand = new();
        protected List<Thread> threads = new();
        protected string connectionString { get; set; }
        protected int threadCount;
        protected int msgCount;
        protected int threadThrottle;
        protected DateTime startTime = DateTime.MinValue;
        protected DateTime endTime = DateTime.MinValue;
        public StringBuilder ReportBuilder { get; set; } = new StringBuilder();

        public BenchmarkTesterBase(string connectionString, int maxThreads, int maxMessages, int threadThrottle) {
            this.connectionString = connectionString;
            this.threadThrottle = threadThrottle;
            threadCount = maxThreads;
            msgCount = maxMessages;
            for (int i = 0; i < 100; i++) {
                usernames.Add($"user{i + 1}");
            }
            runIndexIdentifier = Guid.NewGuid().ToString();
        }

        public override void Run() {
            startTime = DateTime.UtcNow;
            using (LiteDatabase db = new(connectionString)) {
                var dataCollection = db.GetCollection<Data>("data");
                for (int i = 0; i < threadCount; i++) {
                    var thread = new Thread(() => ExecuteBenchmarkThread(dataCollection));
                    threads.Add(thread);
                    thread.Start();
                    Thread.Sleep(threadThrottle);
                }
                foreach (var t in threads) t.Join();
            }
            endTime = DateTime.UtcNow;
        }

        protected abstract void ExecuteBenchmarkThread(ILiteCollection<Data> dataCollection);

        public override string Report() {
            using (LiteDatabase db = new(connectionString)) {
                var dataCollection = db.GetCollection<Data>("data");
                ReportBuilder.AppendLine($"{GetType().Name} Benchmark Report\n");
                ReportBuilder.AppendLine($"Run Index Identifier: {runIndexIdentifier}");
                ReportBuilder.AppendLine($"Anzahl Threads: {threadCount}");
                ReportBuilder.AppendLine($"Anzahl Nachrichten pro Thread: {msgCount}");
                ReportBuilder.AppendLine($"Thread Throttle: {threadThrottle} ms");
                ReportBuilder.AppendLine($"Startzeit: {startTime:HH:mm:ss.fff}");
                ReportBuilder.AppendLine($"Endzeit: {endTime:HH:mm:ss.fff}");
                ReportBuilder.AppendLine($"Dauer: {(endTime - startTime).TotalSeconds:F3} Sekunden\n");
                ReportBuilder.AppendLine($"Benchmark abgeschlossen: {dataCollection.Count()} Requests verarbeitet.");
                var reportData = dataCollection.Find(x => x.runIndexIdentifier == runIndexIdentifier).ToList();
                var groupedData = reportData
                    .GroupBy(d => d.endpoint)
                    .Select(g => new {
                        Endpoint = g.Key,
                        Count = g.Count(),
                        AvgDuration = g.Average(d => d.durationMs),
                        MinDuration = g.Min(d => d.durationMs),
                        MaxDuration = g.Max(d => d.durationMs)
                    })
                    .ToList();
                foreach (var endpointData in groupedData) {
                    ReportBuilder.AppendLine($"Endpoint: {endpointData.Endpoint}");
                    ReportBuilder.AppendLine($"  Requests: {endpointData.Count}");
                    ReportBuilder.AppendLine($"  Avg Duration: {endpointData.AvgDuration:F2} ms");
                    ReportBuilder.AppendLine($"  Min Duration: {endpointData.MinDuration:F2} ms");
                    ReportBuilder.AppendLine($"  Max Duration: {endpointData.MaxDuration:F2} ms");
                }
                return ReportBuilder.ToString();
            }
        }
    }
}

using System.Text;

using ChatApp.Client;

using LiteDB;
namespace Chat.Tests;

public class ChatMicroserviceATester : ATester {
string runIndexIdentifier;

    List<string> usernames = new List<string>();

    // Benchmark: Simuliere parallele Nachrichtenübertragung
    Random rand = new();
    List<Thread> threads = new();

    string connectionString {get;set;}

    int msgCount = 50;
    int threadCount = 100;
    int threadThrottle = 500;

    DateTime startTime = DateTime.MinValue;
    DateTime endTime = DateTime.MinValue;

    public ChatMicroserviceATester(string connectionString, int maxThreads, int maxMessages, int threadThrottle) {
        this.threadThrottle = threadThrottle;
        this.connectionString = connectionString;
        threadCount = maxThreads;
        msgCount = maxMessages;
        for (int i = 0; i < 100; i++) {
            usernames.Add($"user{i + 1}");
        }
        runIndexIdentifier = Guid.NewGuid().ToString();
    }
    public override void Run() {
        startTime = DateTime.UtcNow;
        using (LiteDatabase db = new(connectionString)) {
            var dataCollection = db.GetCollection<Data>("data");
            for (int i = 0; i < threadCount; i++) {
                var thread = new Thread(() => {
                    var messagingClient = new HttpClient {
                        BaseAddress = new(Chat.Common.Addresses.CHAT_MESSAGING_SERVICE),
                        Timeout = TimeSpan.FromSeconds(200)
                    };
                    var historyClient = new HttpClient {
                        BaseAddress = new(Chat.Common.Addresses.CHAT_HISTORY_SERVICE),
                        Timeout = TimeSpan.FromSeconds(200)
                    };
                    var sender = usernames[rand.Next(usernames.Count)];
                    var receiver = usernames[rand.Next(usernames.Count)];
                    if (sender == receiver) return;
                    // Get room information
                    Console.WriteLine("Getting room for: " + sender + " -> " + receiver);
                    var getRoomStart = DateTime.UtcNow;
                    var roomId = messagingClient.GetRoomAsync(sender, [receiver]);
                    roomId.Wait(TimeSpan.FromSeconds(200));
                    var roomDuration = (float)(DateTime.UtcNow - getRoomStart).TotalMilliseconds;
                    dataCollection.Insert(new Data(runIndexIdentifier, "microservice", "/room", getRoomStart, roomDuration, sender, receiver));

                    Console.WriteLine("Getting chat history for: " + sender + " -> " + receiver);
                    var getHistoryStart = DateTime.UtcNow;
                    var historyTask = historyClient.GetChatHistory(roomId.Result);
                    historyTask.Wait(TimeSpan.FromSeconds(200));
                    var histroyDuration = (float)(DateTime.UtcNow - getHistoryStart).TotalMilliseconds;
                    dataCollection.Insert(new Data(runIndexIdentifier, "microservice", "/history", getHistoryStart, histroyDuration, sender, receiver));

                    // Sende mehrere Nachrichten und messe die Zeit pro Nachricht

                    Console.WriteLine("Sending messages for: " + sender + " -> " + receiver);
                    for (int msgIdx = 0; msgIdx < msgCount; msgIdx++) {
                        var msgStart = DateTime.UtcNow;
                        var sendTask = messagingClient.SendMessageAsync(new(sender, roomId.Result, $"{sender}:Message{msgIdx} -> {receiver}", DateTime.UtcNow));
                        try {
                            sendTask.Wait(TimeSpan.FromSeconds(200));
                        } catch (AggregateException ex) {
                            // Fehlerbehandlung, z.B. Timeout
                            continue;
                        }
                        var duration = (float)(DateTime.UtcNow - msgStart).TotalMilliseconds;
                        dataCollection.Insert(new Data(runIndexIdentifier, "microservice", "/send", msgStart, duration, sender, receiver));
                        Thread.Sleep(1000);
                    }
                });
                threads.Add(thread);
                thread.Start();
                Thread.Sleep(threadThrottle);
            }
            // Warten bis alle Threads fertig sind
            foreach (var t in threads) t.Join();
        }
        endTime = DateTime.UtcNow;
    }
    public StringBuilder ReportBuilder { get; set; } = new StringBuilder();
    public override string Report() {
        using (LiteDatabase db = new(connectionString)) {
            var dataCollection = db.GetCollection<Data>("data");
            ReportBuilder.AppendLine($"{nameof(ChatMicroserviceATester)} Benchmark Report\n");
            ReportBuilder.AppendLine($"Run Index Identifier: {runIndexIdentifier}");
            ReportBuilder.AppendLine($"Anzahl Threads: {threadCount}");
            ReportBuilder.AppendLine($"Anzahl Nachrichten pro Thread: {msgCount}");
            ReportBuilder.AppendLine($"Thread Throttle: {threadThrottle} ms");
            ReportBuilder.AppendLine($"Startzeit: {startTime:HH:mm:ss.fff}");
            ReportBuilder.AppendLine($"Endzeit: {endTime:HH:mm:ss.fff}");
            ReportBuilder.AppendLine($"Dauer: {(startTime - endTime).TotalSeconds:F3} Sekunden\n");
            // Ergebnisse ausgeben
            ReportBuilder.AppendLine($"Benchmark abgeschlossen: {dataCollection.Count()} Requests verarbeitet.");
            //Console.WriteLine("Beispielmessungen:");
            //foreach (var d in dataCollection.FindAll().Take(10)) {
            //    Console.WriteLine($"{d.timestamp:HH:mm:ss.fff} | {d.durationMs} ms | {d.sender} -> {d.receiver}");
            //}

            // #report
            var reportData = dataCollection.Find(x => x.runIndexIdentifier == runIndexIdentifier).ToList();

            var groupedData = reportData
                .GroupBy(d => d.endpoint)
                .Select(g => new {
                    Endpoint = g.Key,
                    Count = g.Count(),
                    AvgDuration = g.Average(d => d.durationMs),
                    MinDuration = g.Min(d => d.durationMs),
                    MaxDuration = g.Max(d => d.durationMs)
                })
                .ToList();

            Console.WriteLine("Benchmark Report:");
            foreach (var endpointData in groupedData) {
                ReportBuilder.AppendLine($"Endpoint: {endpointData.Endpoint}");
                ReportBuilder.AppendLine($"  Requests: {endpointData.Count}");
                ReportBuilder.AppendLine($"  Avg Duration: {endpointData.AvgDuration:F2} ms");
                ReportBuilder.AppendLine($"  Min Duration: {endpointData.MinDuration:F2} ms");
                ReportBuilder.AppendLine($"  Max Duration: {endpointData.MaxDuration:F2} ms");
            }


            // Ergebnisse in CSV-Datei schreiben
            //using (var writer = new StreamWriter("benchmark_results.csv"))
            //using (var csv = new CsvWriter(writer, System.Globalization.CultureInfo.InvariantCulture)) {
            //    csv.WriteRecords(dataCollection.FindAll().ToList());
            //}
            return ReportBuilder.ToString();
        }
    }
}

using System.Text;

using ChatApp.Client;
using LiteDB;
namespace Chat.Tests {
    public class ChatMicroserviceATester : BenchmarkTesterBase {
        public ChatMicroserviceATester(string connectionString, int maxThreads, int maxMessages, int threadThrottle)
            : base(connectionString, maxThreads, maxMessages, threadThrottle) {}

        protected override void ExecuteBenchmarkThread(ILiteCollection<Data> dataCollection) {
            var messagingClient = new HttpClient {
                BaseAddress = new(Chat.Common.Addresses.CHAT_MESSAGING_SERVICE),
                Timeout = TimeSpan.FromSeconds(200)
            };
            var historyClient = new HttpClient {
                BaseAddress = new(Chat.Common.Addresses.CHAT_HISTORY_SERVICE),
                Timeout = TimeSpan.FromSeconds(200)
            };
            var sender = usernames[rand.Next(usernames.Count)];
            var receiver = usernames[rand.Next(usernames.Count)];
            if (sender == receiver) return;
            // Get room information
            var getRoomStart = DateTime.UtcNow;
            var roomId = messagingClient.GetRoomAsync(sender, [receiver]);
            roomId.Wait(TimeSpan.FromSeconds(200));
            var roomDuration = (float)(DateTime.UtcNow - getRoomStart).TotalMilliseconds;
            dataCollection.Insert(new Data(runIndexIdentifier, "microservice", "/room", getRoomStart, roomDuration, sender, receiver));

            // Get chat history
            var getHistoryStart = DateTime.UtcNow;
            var historyTask = historyClient.GetChatHistory(roomId.Result);
            historyTask.Wait(TimeSpan.FromSeconds(200));
            var histroyDuration = (float)(DateTime.UtcNow - getHistoryStart).TotalMilliseconds;
            dataCollection.Insert(new Data(runIndexIdentifier, "microservice", "/history", getHistoryStart, histroyDuration, sender, receiver));

            // Send messages
            for (int msgIdx = 0; msgIdx < msgCount; msgIdx++) {
                var msgStart = DateTime.UtcNow;
                var sendTask = messagingClient.SendMessageAsync(new(sender, roomId.Result, $"{sender}:Message{msgIdx} -> {receiver}", DateTime.UtcNow));
                try {
                    sendTask.Wait(TimeSpan.FromSeconds(200));
                } catch (AggregateException) {
                    continue;
                }
                var duration = (float)(DateTime.UtcNow - msgStart).TotalMilliseconds;
                dataCollection.Insert(new Data(runIndexIdentifier, "microservice", "/send", msgStart, duration, sender, receiver));
                Thread.Sleep(1000);
            }
        }
    }
}

